import React, { Component,useState } from 'react';
import md5 from 'md5';
import 'bootstrap/dist/css/bootstrap.min.css';
import  { Redirect } from 'react-router-dom'
import axios from 'axios';
import '../css/Login.css';

const baseUrl="https://localhost:44313/api/Users";


class Login extends Component {
  
  constructor(props){
    super(props);
    this.state={
    username:'',
    password:''
    }
  }
  iniciarSesion=()=>{

    axios.get(baseUrl+'/${this.state.username}/${md5(this.state.password)}').then(response=>{
      alert('Bienvenido')
      console.log('u:'+this.state.username);
      console.log('p:'+this.state.password);
    }).catch(error=>{
      console.log(error.message);
    })
    }
      render() {
        return (
          <div>
            <table>
              <thead>
              <title>"Login"</title>
              </thead>
              <tbody>
                <tr>
                  <td>
                    usuario:
                  </td>
                  <td>
                  <input
              type="text"
              className="form-control"
              name="userName"
              onChange = {(event,newValue) => this.setState({username:newValue})}
            />
                  </td>
                </tr>
                <tr>
                  <td>
                    Password:
                  </td>
                  <td>
                  <input
              type="password"
              className="form-control"
              name="password"
              onChange = {(event,newValue) => this.setState({password:newValue})}
            />
                  </td>
                </tr>
              </tbody>
              
            </table>
            <button className="btn btn-primary" onClick={()=>this.iniciarSesion()}>Iniciar Sesi√≥n</button>

          </div>
        );
      }
    
}
export default Login;
