import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import axios from "axios";
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';

const url="https://localhost:44313/api/Agencies/";
const url_auth="https://v45hh4g3q5.execute-api.us-east-1.amazonaws.com/Dev/authentication";
const url_agencies="https://v45hh4g3q5.execute-api.us-east-1.amazonaws.com/Dev/agencias";
const username_auth="techuser";
const password_auth="TechUser123";

class App extends Component {
state={
  data:[],
  form:{
    id: '',
    name: '',
    status: '',
    city: '',
    state: ''
  }
}

peticionGet=()=>{
axios.get(url).then(response=>{
  this.setState({data: response.data});
}).catch(error=>{
  console.log(error.message);
})
}

peticionPost=async()=>{
  delete this.state.form.id;
 await axios.post(url,this.state.form).then(response=>{
    this.peticionGet();
  }).catch(error=>{
    console.log(error.message);
  })
}





seleccionarEmpresa=(empresa)=>{
  this.setState({
    tipoModal: 'actualizar',
    form: {
      id: empresa.id,
      nombre: empresa.nombre,
      pais: empresa.pais,
      capital_bursatil: empresa.capital_bursatil
    }
  })
}

handleChange=async e=>{
e.persist();
await this.setState({
  form:{
    ...this.state.form,
    [e.target.name]: e.target.value
  }
});
console.log(this.state.form);
}

  componentDidMount() {
    this.peticionGet();
  }
  

  render(){
    const {form}=this.state;
  return (
    <div className="App">
    <br /><br /><br />
  <button className="btn btn-success" onClick={()=>{this.setState({form: null, tipoModal: 'insertar'}); this.modalInsertar()}}>Agregar Empresa</button>
  <br /><br />
    <table className="table ">
      <thead>
        <tr>
          <th>ID</th>
          <th>NAME</th>
          <th>STATUS</th>
          <th>CITY</th>
          <th>STATE</th>
        </tr>
      </thead>
      <tbody>
        {this.state.data.map(agencies=>{
          return(
            <tr>
          <td>{agencies.id}</td>
          <td>{agencies.name}</td>
          <td>{agencies.status}</td>
          <td>{agencies.city}</td>
          <td>{agencies.state}</td>
          </tr>
          )
        })}
      </tbody>
    </table>

   
  </div>



  );
}
}
export default App;
