import React, {useState, useEffect} from 'react';
import md5 from 'md5';
import 'bootstrap/dist/css/bootstrap.min.css';
import Cookies from 'universal-cookie';
import axios from 'axios';
import '../css/Login.css';

function Login(props) {

const baseUrl="https://localhost:44339/api/Users";
const cookies = new Cookies();

const [form, setForm]=useState({
  userName:'',
  password: ''
});
  const handleChange=e=>{
 const {name, value} = e.target;
 setForm({
   ...form,
   [name]: value
 });
  }

  const iniciarSesion=async()=>{
    await axios.get(baseUrl+`/${form.userName}/${md5(form.password)}`)
    .then(response=>{
      return response.data;
    }).then(response=>{
      if(response.length>0){
        var respuesta=response[0];
        cookies.set('id', respuesta.id, {path: '/'});
        cookies.set('firstName', respuesta.firstName, {path: '/'});
        cookies.set('lastName', respuesta.lastName, {path: '/'});
        cookies.set('code', respuesta.code, {path: '/'});
        cookies.set('email', respuesta.email, {path: '/'});
        cookies.set('userName', respuesta.userName, {path: '/'});
        cookies.set('password', respuesta.password, {path: '/'});
        alert("Bienvenido: "+respuesta.firstName+" "+respuesta.lastName);
        props.history.push('/menu');
      }else{
        alert('El usuario o la contrase침a no son correctos');
      }
    })
    
    .catch(error=>{
      console.log(error);
    })
  }

  useEffect(()=>{
if(cookies.get('id')){
  props.history.push('/menu');
}
  },[]);

    return (
        <div className="containerPrincipal">
        <div className="containerLogin">
          <div className="form-group">
            <label>Usuario: </label>
            <br />
            <input
              type="text"
              className="form-control"
              name="userName"
              onChange={handleChange}
            />
            <br />
            <label>Contrase침a: </label>
            <br />
            <input
              type="password"
              className="form-control"
              name="password"
              onChange={handleChange}
            />
            <br />
            <button className="btn btn-primary" onClick={()=>iniciarSesion()}>Iniciar Sesi칩n</button>
          </div>
        </div>
      </div>
    );
}

export default Login;
